sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
	if(!g_form.getValue('valor_unitario')){
		g_form.addInfoMessage("Preencha o valor Unitario")
		return
	}
	var currency = g_form.getValue('valor_unitario').split(';')[1].replace('.','').replace(',','.');
	var total = Number(g_form.getValue('quantidade')) * Number(currency);

	g_form.setValue('valor_total',total)

	//Exemplo de como setar aviso 
	//g_form.addInfoMessage('Funcineu  '+total);

   //Type appropriate comment here, and begin script below

}
